import React from "react"
import { useState, useEffect } from "react";
import { getPermissions, deletePermission } from "./PermissionActions";
import AddEditPermission from "./AddEditPermission";

const PermissionList = () => {
  const [permissions, setPermissions] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editingPermission, setEditingPermission] = useState(null);

  const fetchPermissions = async () => {
    const perms = await getPermissions();
    setPermissions(perms);
  };

  useEffect(() => {
    fetchPermissions();
  }, []);

  const handleDelete = async (id: number) => {
    if (window.confirm("Are you sure you want to delete this permission?")) {
      await deletePermission(id);
      fetchPermissions();
    }
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Permissions Management</h1>
      <button
        className="bg-green-500 text-white px-4 py-2 rounded mb-4"
        onClick={() => {
          setEditingPermission(null);
          setIsModalOpen(true);
        }}
      >
        Add Permission
      </button>
      <table className="min-w-full bg-white border">
        <thead>
          <tr>
            <th className="py-2 px-4 border">ID</th>
            <th className="py-2 px-4 border">Name</th>
            <th className="py-2 px-4 border">Actions</th>
          </tr>
        </thead>
        <tbody>
          {permissions.map((perm: any) => (
            <tr key={perm.id}>
              <td className="py-2 px-4 border">{perm.id}</td>
              <td className="py-2 px-4 border">{perm.name}</td>
              <td className="py-2 px-4 border">
                <button
                  className="bg-blue-500 text-white px-4 py-1 rounded mr-2"
                  onClick={() => {
                    setEditingPermission(perm);
                    setIsModalOpen(true);
                  }}
                >
                  Edit
                </button>
                <button
                  className="bg-red-500 text-white px-4 py-1 rounded"
                  onClick={() => handleDelete(perm.id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <AddEditPermission
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSave={fetchPermissions}
        permission={editingPermission}
      />
    </div>
  );
};

export default PermissionList;
